enum UnwrappingError: Error {
 case initialNil
 case nestedNil
}

func unwrapNestedOptionals(initialOptional: Int??) throws -> Int {
 guard let firstUnwrap = initialOptional else {
 throw UnwrappingError.initialNil
 }

 guard let finalUnwrap = firstUnwrap else {
 throw UnwrappingError.nestedNil
 }

 return finalUnwrap
}

do {
 let result = try unwrapNestedOptionals(initialOptional: 10)
 print(
"Result: 

(결과)
")
} catch UnwrappingError.initialNil {
 print(
"초기 옵션은 nil이었습니다.
")
} catch UnwrappingError.nestedNil {
 print(
"중첩된 옵션은 nil이었습니다.
")
